import path from 'path';
const ROOT = path.join(__dirname, '../../../htdocs');
export const htdocsRoot = () => ROOT;
export function getHtmlFile(file) {
    return path.join(ROOT, file || '');
}
export function getImgFile(file) {
    return path.join(ROOT, 'img', file || '');
}
export function getJsFile(file) {
    return path.join(ROOT, 'js', file || '');
}
