import { expect, test } from 'vitest';
import { request } from '../../../__tests__/utils';
test('ok', async () => {
    const r1 = await request('http://attachment.test.whistlejs.com/index.html');
    expect(r1.res.headers).toHaveProperty('content-disposition', 'attachment; filename="index.html"');
    const r2 = await request({
        method: 'put',
        url: 'https://attachment.test.whistlejs.com/index2.html'
    });
    expect(r2.res.headers).toHaveProperty('content-disposition', 'attachment; filename="index2.html"');
});
