import { describe, expect, test } from 'vitest';
import { request } from '../../../__tests__/utils';
describe('auth', () => {
    test('ok', async () => {
        const r1 = await request('http://au2th.test.whistlejs.com/index.html');
        expect(r1.data.headers).not.toHaveProperty('authorization');
        const r2 = await request({
            method: 'put',
            url: 'https://auth.test.whistlejs.com/index2.html'
        });
        expect(r2.data.headers).toHaveProperty('authorization');
    });
});
