import { describe, expect, test } from 'vitest';
import { request } from '../../../__tests__/utils';
describe('css', () => {
    test('ok', async () => {
        const r1 = await request('http://css1.test.whistlejs.com/index.html?resBody=_');
        expect(r1.data).toBe('_\r\n<style>css</style>');
        const r2 = await request({
            method: 'post',
            url: 'https://css2.test.whistlejs.com/index.html?resBody=_'
        });
        expect(r2.data).toBe('_\r\ncss');
        const r3 = await request({
            method: 'post',
            url: 'https://css3.test.whistlejs.com/index.html?resBody=_'
        });
        expect(r3.data).toBe('_');
    });
});
