import { describe, expect, test } from 'vitest';
import { request } from '../../../__tests__/utils';
describe('disable', () => {
    test('ok', async () => {
        const r1 = await request('http://disable.test.whistlejs.com/index.html');
        expect(r1.res.headers).toHaveProperty('cache-control', 'no-cache');
        const r2 = await request({
            method: 'post',
            url: 'https://disable.test.whistlejs.com/index.html'
        });
        expect(r2.res.headers).toHaveProperty('cache-control', 'no-cache');
        expect(r2.res.headers).not.toHaveProperty('set-cookie');
    });
});
