import { describe, expect, test } from 'vitest';
import { request } from '../../../__tests__/utils';
describe('html', () => {
    test('ok', async () => {
        const r1 = await request('http://html1.test.whistlejs.com/index.html?resBody=_');
        expect(r1.data).toBe('_\r\nhtml');
        const r2 = await request('http://prependhtml.whistlejs.com/index.html?resBody=_');
        expect(r2.data).toBe(['<!DOCTYPE html>', '-1', '-2', '1', '2', '3', '4'].join('\r\n'));
        const r3 = await request({
            method: 'post',
            url: 'https://html2.test.whistlejs.com/index.html?resBody=_'
        });
        expect(r3.data).toBe('_');
        const r4 = await request({
            method: 'post',
            url: 'http://html3.test.whistlejs.com/index.html?resBody=_'
        });
        expect(r4.data).toBe('_');
    });
});
