import { describe, expect, test } from 'vitest';
import { request } from '../../../__tests__/utils';
describe('log', () => {
    test('ok', async () => {
        const r1 = await request('http://log1.test.whistlejs.com/index.html?resBody=_');
        expect(r1.data.length).toBeGreaterThan(500);
        expect(r1.data).toContain('<script>log</script>\r\n_');
        const r2 = await request({
            method: 'post',
            url: 'https://log2.test.whistlejs.com/index.html?resBody=_'
        });
        expect(r2.data.length).toBeGreaterThan(500);
        expect(r2.data).toContain('_\r\nlog');
        const r3 = await request({
            method: 'post',
            url: 'https://log3.test.whistlejs.com/index.html?resBody=_'
        });
        expect(r3.data).toBe('_');
    });
});
