import { describe, expect, test } from 'vitest';
import { request } from '../../../__tests__/utils';
describe('method', () => {
    test('ok', async () => {
        const r1 = await request('http://test.whistlejs.com/index.html');
        expect(r1.data.method).toBe('GET');
        const r2 = await request({
            method: 'put',
            url: 'https://test.whistlejs.com/index.html'
        });
        expect(r2.data.method).toBe('PUT');
    });
});
