import { describe, expect, test } from 'vitest';
import { request } from '../../../__tests__/utils';
describe('range', () => {
    test('ok', async () => {
        const r1 = await request({
            uri: 'http://range1.whistlejs.com/index.html',
            headers: {
                range: 'bytes=2-3,5-6'
            }
        });
        expect(r1.data).toBe(23456);
        const r2 = await request({
            url: 'http://range2.whistlejs.com/',
            headers: {
                range: 'bytes=2-3,1-8'
            }
        });
        expect(r2.data).toBe(12345678);
    });
});
