import { describe, expect, test } from 'vitest';
import { request } from '../../../__tests__/utils';
describe('redirect', () => {
    test('ok', async () => {
        const r1 = await request('https://redirect.test.whistlejs.com/index.html');
        expect(r1.data).toHaveProperty('url', 'http://test.whistlejs.com/');
        const r2 = await request({
            method: 'post',
            url: 'http://redirect.test.whistlejs.com/index.html'
        });
        expect(r2.data).toHaveProperty('url', 'http://test.whistlejs.com/');
    });
});
