import { describe, expect, test } from 'vitest';
import { request } from '../../../__tests__/utils';
describe('referer', () => {
    test('ok', async () => {
        const r1 = await request('http://referer.test.whistlejs.com/index.html');
        expect(r1.data.headers).toHaveProperty('referer', 'xxx');
        const r2 = await request({
            url: 'https://referer.test.whistlejs.com/index.html',
            method: 'post'
        });
        expect(r2.data.headers).toHaveProperty('referer', 'xxx');
    });
});
