import { describe, expect, test } from 'vitest';
import { request } from '../../../__tests__/utils';
describe('replaceStatus', () => {
    test('ok', async () => {
        const r1 = await request('http://replacestatus.test.whistlejs.com/index.html');
        expect(r1.res.statusCode).toBe(500);
        const r2 = await request({
            method: 'post',
            url: 'https://replacestatus.test.whistlejs.com/index.html'
        });
        expect(r2.res.statusCode).toBe(500);
    });
});
