import { describe, expect, test } from 'vitest';
import { request } from '../../../__tests__/utils';
describe('reqAppend', () => {
    test('ok', async () => {
        const r1 = await request({
            url: 'https://reqappend.test.whistlejs.com/',
            method: 'post'
        });
        expect(r1.data.body).toBe('append\r\nappend');
        const r2 = await request({
            url: 'http://reqappend.test.whistlejs.com/',
            method: 'post'
        });
        expect(r2.data.body).toBe('append\r\nappend');
    });
});
