import { describe, expect, test } from 'vitest';
import { request } from '../../../__tests__/utils';
describe('reqCors', () => {
    test('ok', async () => {
        const r1 = await request('http://reqcors.test.whistlejs.com/index.html');
        expect(r1.data.headers).toHaveProperty('origin', '*');
        expect(r1.data.headers).toHaveProperty('access-control-request-method', 'POST');
        expect(r1.data.headers).toHaveProperty('access-control-request-headers', 'x-test');
        const r2 = await request({
            method: 'post',
            url: 'https://reqcors.test.whistlejs.com/index.html'
        });
        expect(r2.data.headers).toHaveProperty('origin', '*');
        expect(r2.data.headers).toHaveProperty('access-control-request-method', 'POST');
        expect(r2.data.headers).toHaveProperty('access-control-request-headers', 'x-test');
    });
});
