import { describe, expect, test } from 'vitest';
import { request } from '../../../__tests__/utils';
describe('reqReplace', () => {
    test('ok', async () => {
        const r1 = await request({
            url: 'https://reqreplace.test.whistlejs.com/',
            method: 'post',
            body: 'testxxx'
        });
        expect(r1.data.body).toBe('abcxxx');
        const r2 = await request({
            url: 'http://reqreplace.test.whistlejs.com/',
            method: 'post',
            body: 'test123'
        });
        expect(r2.data.body).toBe('abc123');
    });
});
