import { describe, expect, test } from 'vitest';
import { request } from '../../../__tests__/utils';
describe('resAppend', () => {
    test('ok', async () => {
        const r1 = await request({
            url: 'https://resappend.test.whistlejs.com/?resBody=',
            method: 'post'
        });
        expect(r1.data).toBe('append\r\nappend');
        const r2 = await request('http://resappend.test.whistlejs.com/?resBody=');
        expect(r2.data).toBe('append\r\nappend');
    });
});
