import { describe, expect, test } from 'vitest';
import { request } from '../../../__tests__/utils';
describe('resHeaders', () => {
    test('ok', async () => {
        const r1 = await request('http://resheaders.test.whistlejs.com/index.html');
        expect(r1.res.headers).toHaveProperty('x-test1', 'value1');
        expect(r1.res.headers).toHaveProperty('x-test2', 'value2');
        expect(r1.res.headers).toHaveProperty('x-testn', 'valueN');
        const r2 = await request({
            method: 'post',
            url: 'https://resheaders.test.whistlejs.com/index.html'
        });
        expect(r2.res.headers).toHaveProperty('x-test1', 'value1');
        expect(r2.res.headers).toHaveProperty('x-test2', 'value2');
        expect(r2.res.headers).toHaveProperty('x-testn', 'valueN');
    });
});
