import { describe, expect, test } from 'vitest';
import { request } from '../../../__tests__/utils';
describe('resReplace', () => {
    test('ok', async () => {
        const r1 = await request({
            url: 'https://resreplace.test.whistlejs.com/?resBody=123test',
            method: 'post'
        });
        expect(r1.data).toBe('123abc');
        const r2 = await request('http://resreplace.test.whistlejs.com/?resBody=test123');
        expect(r2.data).toBe('abc123');
    });
});
