import { describe, expect, test } from 'vitest';
import { request } from '../../../__tests__/utils';
describe('script', () => {
    test('ok', async () => {
        const r1 = await request('http://reqscript.w2.org/index.html?doNotParseJson');
        expect(r1.data).toBe('123');
        expect(r1.res.headers['x-test']).toBe('123');
        const r2 = await request({
            url: 'http://reqscript.w2.org/index.html?doNotParseJson',
            method: 'POST',
            body: 'test'
        });
        expect(r2.data).toBe('test');
        expect(r2.res.headers['x-test']).toBe('test');
    });
});
