import { describe, expect, test } from 'vitest';
import { request } from '../../../__tests__/utils';
describe('tpl', () => {
    test('ok', async () => {
        const r1 = await request('http://tpl.test.whistlejs.com/index.html?resBody=%7Bcallback%7D(%7Bec%3A%200%7D)&callback=test');
        expect(r1.data).toBe('test({ec: 0})');
        const r2 = await request('http://tpl.test.whistlejs.com/index.html?doNotParseJson&callback=test');
        expect(r2.data).toBe('test({ec: 0})');
        const r3 = await request({
            method: 'post',
            url: 'https://tpl2.test.whistlejs.com/?resBody=%7Bcallback%7D(%7Bec%3A%200%7D)&callback=test'
        });
        expect(r3.data).toBe('test({ec: 0})');
        const r4 = await request('http://xtpl.test.whistlejs.com/index2.html');
        expect(r4.data).toHaveProperty('type', 'server');
    });
});
