import { describe, expect, test } from 'vitest';
import { request } from '../../../__tests__/utils';
describe('urlReplace', () => {
    test('ok', async () => {
        const r1 = await request('http://urlreplace.host.test.whistlejs.com/index.html?name=aven&test=abc');
        expect(r1.data.url).toBe('/index.html?user=ttven&test=ttbc');
        const r2 = await request({
            method: 'post',
            url: 'http://urlreplace.host.test.whistlejs.com/index.html?name=aven&test=abc'
        });
        expect(r2.data.url).toBe('/index.html?user=ttven&test=ttbc');
    });
});
