import { describe, test } from 'vitest';
import { getTextBySize, request } from '../../../__tests__/utils';
describe('write', () => {
    test('ok', async () => {
        const r1 = await request('http://reqwrite.test.whistlejs.com/index.html');
        const r2 = await request({
            url: 'https://reqwrite.test.whistlejs.com/index.html',
            method: 'post',
            body: getTextBySize(32)
        });
        const r3 = await request('https://reswrite2.test.whistlejs.com/index.html');
        const r4 = await request({
            url: 'http://reswrite2.test.whistlejs.com/index.html',
            method: 'post',
            body: getTextBySize(128)
        });
    });
});
