import fs from 'node:fs';
import { describe, expect, test } from 'vitest';
import * as zlib from '../zlib';
const emptyGzipContent = fs.readFileSync(require.resolve('../../../../__tests__/assets/files/empty.txt'));
const testGzipContent = fs.readFileSync(require.resolve('../../../../__tests__/assets/files/test.txt'));
describe('zlib', () => {
    test('success', () => {
        zlib.gunzip(emptyGzipContent, function (err, content) {
            expect(content.length).toBe(0);
        });
        zlib.gunzip(testGzipContent, function (err, content) {
            expect(content + '').toBe('test');
        });
    });
});
